 public void carregaDados() throws Exception{
        DBController db = new DBController();
        try{
            db.conectar();
            ResultSet rset= db.carregaUsuario();
            DefaultTableModel model= new DefaultTableModel(new String[]{"CPF","Nome","Email","Telefone"},0);
            while(rset.next()){
                model.addRow(new Object[]{rset.getString("cpf"),rset.getString("nome"),rset.getString("email"),rset.getString("telefone")});
            }
            tblUsuarios.setModel(model);
            db.desconectar();

        }
        catch(Exception e){
            JOptionPane.showMessageDialog(null, e.getMessage());
        }
        
    }
