    private void BtnSearchActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BtnSearchActionPerformed
        
        DBController db = new DBController();
        String nome = edtName.getText();
        try{
            db.conectar();
            ResultSet rset= db.buscaUsuario(nome);
            DefaultTableModel model= new DefaultTableModel(new String[]{"Nome","Email","Telefone"},0);
            while(rset.next()){
                model.addRow(new Object[]{rset.getString("nome"),rset.getString("email"),rset.getString("telefone")});
            }
            tblUsuarios.setModel(model);
            db.desconectar();
            
        }
        catch(Exception e){
            JOptionPane.showMessageDialog(null, e.getMessage());
        } 
    
    }//GEN-LAST:event_BtnSearchActionPerformed

